$(function(){$(".event-content--4 .box").mousemove(function(t){if($(".tournament-container").is(":hidden")){var e=$(this).offset(),n=5,o=t.pageX-e.left,i=t.pageY-e.top,s=($(this).height()/2-i)/($(this).height()/2)*n*2,a=-1*($(this).width()/2-o)/($(this).width()/2)*n;$(".event-content--4 .box__box").css({transform:"rotateX("+s+"deg) rotateY("+a+"deg)",transition:"none"})}}),$(".event-content--4 .box").mouseleave(function(t){$(".event-content--4 .box__box").css({transform:"rotateX(0deg) rotateY(0deg)",transition:"1000ms all"})})});